package de.bergwerklabs.framework.bedrock.api.session;

import java.io.UnsupportedEncodingException;
import java.security.SecureRandom;
import java.util.Base64;

/**
 * Created by Yannic Rieger on 11.11.2017.
 * <p>
 *
 * @author Yannic Rieger
 */
public class GameId {

    private static String cachedId;

    /**
     * Generates a random game id.
     *
     * @return Base64 encoded string based on a randomly generated byte array.
     */
    public static String create() {
        if (cachedId != null) return cachedId;
        cachedId = generateId();
        return cachedId;
    }

    /**
     * Creates Base64 encoded string based on a randomly generated byte array.
     *
     * @return Base64 encoded string based on a randomly generated byte array.
     */
    private static String generateId() {
        byte[] bytes = new byte[8];
        new SecureRandom().nextBytes(bytes);

        byte[] base64 = Base64.getEncoder().encode(bytes);

        try {
           return new String(base64, "UTF-8").replace("/", "_")
                                                .replace("+", "-")
                                                .replace("=", "");
        }
        catch (UnsupportedEncodingException e) {
            e.printStackTrace();
            return null;
        }
    }
}
